  grid: {
    width: '100%',
    margin: '0px',
  },

}));

function App() {
  const classes = useStyles();
  const name = "Hello, I'm Manh Sy.";
  const introduction = <span>Iam a fourth year Computer Science student from CSU, Sacraemento<br/>My technical skills include Java, Javascript, & Python</span>
