        <Grid container md = "6" direction="column-reverse" justify="flex-start" alignItems="flex-start">
            <Grid item xs = {1} md = {10}>
                  <Introduction name = {name} description = {introduction}/>
            </Grid> 
        </Grid>
        <Grid container md = "6"direction="column" justify="flex-end" alignItems="flex-end" spacing = {3}>
            <Grid item xs = {1} md = {10} lg = {10}>
              <ProjectCards info = {emailRocketProject}/> 
