            <Grid item xs = {1} md = {10} lg = {10} >
              <ProjectCards info = {yelpRatingProject}/> 
            </Grid>
            <Grid item xs = {1} md = {10} lg = {10} >
              <ProjectCards info = {networkIntrusionProject}/>
            </Grid>
            <Grid item xs = {1} md = {10} lg = {10} >
