import ProjectCards from './components/ProjectCards';

//images
import emailRocketImage from './image/source.gif'
import yelpImage from './image/yelp.jpeg'
import networkIntrusionImage from './image/Network-Intrusion.jpeg'
import computerVisionImage from './image/computerVision.jpeg'

const useStyles = makeStyles ((theme) => ({
  grid: {
    width: '100%',
    margin: '10px',
    align: 'right'
  }

}));

function App() {
  const classes = useStyles();
  const name = "Hello, I'm Manh Sy.";
  const introduction = <span>Iam a fourth year Computer Science student from CSU, Sacraemento<br/>My technical skills include Java, Javascript, & Python</span>
  const emailRocketProject = {
    name: "Email-rocket",
