import React from 'react';
import {Grid} from "@material-ui/core";

import Introduction from './Introduction';
import ProjectButton from './ProjectButton';
const name = (
    <>
      <p style ={{marginTop: 45}}>Hello, I'm</p>
      <p style ={{marginTop: -120}}>Manh Sy.</p>
    </>
  );
const introduction = <span>I’m a Staff Software Engineer currently working as Tech Lead of the Doc Squad at Algolia. 
    I mostly do front-end development, and I’m a Vue.js and CSS nerd. I can't shut up about test-driven development and utility-first 
    CSS. I also share what I learn on my blog frontstuff.io, or at meetups and conferences.</span>
function IntroductionGrid(){
    return (
        <Grid container sx = "12" lg = "5" md = "12"  direction="column" justify="flex-start" alignItems="flex-start" style={{ position: "sticky", top: "1rem" }}>
            <div style = {{marginLeft: 80}}> 
                <Grid item>
                <Introduction name = {name} description = {introduction}/>
                </Grid> 
            </div>
            <Grid item style = {{marginLeft: 80, marginTop: 90}}>
                <ProjectButton/>
            </Grid> 
      </Grid>
    );
}

export default IntroductionGrid;
