import React, { Component } from "react";
import { MDBContainer,  MDBNav, MDBNavItem, MDBNavLink, MDBIcon } from "mdbreact";
import "./Pills.css";

class PillsWithIcon extends Component {
    state = {
      items: {
      icons: "1",
      iconsRight: "1",
      }
    }

    togglePills = (type, tab) => e => {
      e.preventDefault();
      if (this.state.items[type] !== tab) {
        let items = { ...this.state.items };
        items[type] = tab;
        this.setState({
          items
        });
      }
    }}

    function technologyPill( {lang} ){
        return(
            <MDBContainer>
                <MDBNav pills className="nav-justified pills-rounded pills-outline-red">
                    <MDBNavItem>
                        <MDBNavLink
                        link
                        to="#"
                        active={this.state.items["iconsRight"] === "1"}
                        onClick={this.togglePills("iconsRight", "1")}
                        >
                        {lang} <MDBIcon far icon="grin" />
                        </MDBNavLink>
                    </MDBNavItem>
                </MDBNav>
            </MDBContainer>
        );
    }

    export default technologyPill;
